INSERT INTO `modx_site_htmlsnippets` VALUES (1,1,0,'Head','Head of site with scripts and styles',0,2,0,'<meta charset=\"[[++modx_charset]]\">\r\n<meta http-equiv=\"X-UA-Compatible\" content=\"IE=edge\">\r\n<meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\r\n<meta name=\"description\" content=\"\">\r\n<meta name=\"author\" content=\"\">\r\n<base href=\"[[++site_url]]\" />\r\n<title>[[*longtitle:default=`[[*pagetitle]]`]] - [[++site_name]]</title>\r\n\r\n<!-- You can add theme from bootswatch.com: just add it into &cssSources=``.\r\nFor example: [[++assets_url]]components/themebootstrap/css/cerulean/bootstrap-theme.min.css-->\r\n[[MinifyX?\r\n	&minifyCss=`1`\r\n	&registerCss=`1`\r\n	&cssSources=`\r\n		[[++assets_url]]components/themebootstrap/css/bootstrap.min.css,\r\n		[[++assets_url]]components/themebootstrap/css/add.css\r\n	`\r\n	&minifyJs=`1`\r\n	&registerJs=`1`\r\n	&jsSources=`\r\n		[[++assets_url]]components/themebootstrap/js/bootstrap.min.js\r\n	`\r\n]]\r\n\r\n<script src=\"[[++assets_url]]components/themebootstrap/js/jquery.min.js\"></script>\r\n\r\n<!-- HTML5 shim and Respond.js IE8 support of HTML5 elements and media queries -->\r\n<!--[if lt IE 9]>\r\n<script src=\"https://oss.maxcdn.com/libs/html5shiv/3.7.0/html5shiv.js\"></script>\r\n<script src=\"https://oss.maxcdn.com/libs/respond.js/1.3.0/respond.min.js\"></script>\r\n<![endif]-->\r\n',0,NULL,0,'assets/components/themebootstrap/elements/chunks/chunk.head.tpl'),(2,1,0,'Navbar','Navbar with main navigation',0,2,0,'<!-- Static navbar -->\r\n<div class=\"navbar navbar-default navbar-static-top\" role=\"navigation\">\r\n	<div class=\"container\">\r\n		<div class=\"navbar-header\">\r\n			<button type=\"button\" class=\"navbar-toggle\" data-toggle=\"collapse\" data-target=\".navbar-collapse\">\r\n				<span class=\"sr-only\">Toggle navigation</span>\r\n				<span class=\"icon-bar\"></span>\r\n				<span class=\"icon-bar\"></span>\r\n				<span class=\"icon-bar\"></span>\r\n			</button>\r\n			<a class=\"navbar-brand\" href=\"/\">[[++site_name]]</a>\r\n		</div>\r\n		<div class=\"navbar-collapse collapse\">\r\n			<ul class=\"nav navbar-nav\">\r\n				[[pdoMenu?\r\n					&startId=`0`\r\n					&level=`2`\r\n					&tplParentRow=`@INLINE\r\n					<li class=\"[[+classnames]] dropdown\">\r\n						<a href=\"#\" class=\"dropdown-toggle\" data-toggle=\"dropdown\" [[+attributes]]>[[+menutitle]]<b class=\"caret\"></b></a>\r\n						<ul class=\"dropdown-menu\">[[+wrapper]]</ul>\r\n					</li>`\r\n					&tplOuter=`@INLINE [[+wrapper]]`\r\n				]]\r\n			</ul>\r\n		</div><!--/.nav-collapse -->\r\n	</div>\r\n</div>',0,NULL,0,'assets/components/themebootstrap/elements/chunks/chunk.navbar.tpl'),(3,1,0,'Footer','Chunk with footer',0,2,0,'<footer>\r\n	<div class=\"row\">\r\n		<div class=\"col-lg-8\">\r\n			<p><small>\r\n				total time: [^t^]<br/>\r\n				query time: [^qt^]<br/>\r\n				queries: [^q^]\r\n			</small></p>\r\n		</div>\r\n		<div class=\"col-lg-4\">\r\n			<p class=\"\">&copy;2013 [[++site_name]]</p>\r\n			<a href=\"http://bezumkin.ru/\">[[++cultureKey:is=`ru`:then=`Василий Наумкин`:else=`Vasiliy Naumkin`]]</a>\r\n		</div>\r\n	</div>\r\n</footer>',0,NULL,0,'assets/components/themebootstrap/elements/chunks/chunk.footer.tpl'),(4,1,0,'Content.main','Content of main page',0,2,0,'<h3>[[++site_name]]</h3>\n<h2>Каталог</h2>\n[[*content]]\n\n',0,'a:0:{}',0,'assets/components/themebootstrap/elements/chunks/chunk.content.main.tpl'),(22,1,0,'tovar','',0,0,0,'[[+pagetitle]]<br>\nЦена: [[!Price? &doc_id=`[[+id]]`]] \n<hr>',0,'a:0:{}',0,''),(5,1,0,'Content.inner','Content of inner pages',0,2,0,'<h3>[[*longtitle:default=`[[*pagetitle]]`]]</h3>\n\n[[*content]]',0,NULL,0,'assets/components/themebootstrap/elements/chunks/chunk.content.inner.tpl'),(6,1,0,'Crumbs','Breadcrumbs navigation',0,2,0,'[[pdoCrumbs?\n	&showAtHome=`0`\n	&showHome=`1`\n	&outputSeparator=``\n	&tpl=`@INLINE <li><a href=\"[[+link]]\">[[+menutitle]]</a></li>`\n	&tplCurrent=`@INLINE <li class=\"active\">[[+menutitle]]</li>